mapper.update:
update admin
username = #{username,jdbcType=VARCHAR}, password = #{password,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, phone = #{phone,jdbcType=VARCHAR},
role = #{role,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, username, password, phone, email, role
from admin
where id = #{id,jdbcType=INTEGER}

select id, username, password, phone, email, role
from admin
where username = #{username,jdbcType=VARCHAR}
