mapper.update:
update bx_project
set name = #{name,jdbcType=VARCHAR},
  start_time = #{startTime,jdbcType=TIMESTAMP},
  end_time = #{endTime,jdbcType=TIMESTAMP},
  cost = #{cost,jdbcType=REAL}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, start_time, end_time, cost
from bx_project
where id = #{id,jdbcType=INTEGER}
